import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeftIcon, CloudArrowUpIcon } from '@heroicons/react/24/outline';

const Upload = () => {
  const navigate = useNavigate();
  const [dragActive, setDragActive] = useState(false);
  const [files, setFiles] = useState([]);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const droppedFiles = Array.from(e.dataTransfer.files);
    setFiles(prevFiles => [...prevFiles, ...droppedFiles]);
  };

  const handleFileInput = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(prevFiles => [...prevFiles, ...selectedFiles]);
  };

  const removeFile = (indexToRemove) => {
    setFiles(prevFiles => prevFiles.filter((_, index) => index !== indexToRemove));
  };

  const handleUpload = () => {
    // Implement your upload logic here
    console.log('Uploading files:', files);
  };

  return (
    <div className="min-h-screen bg-secondary-50 p-6">
      <div className="mb-6">
        <button
          onClick={() => navigate('/')}
          className="inline-flex items-center text-secondary-600 hover:text-secondary-900"
        >
          <ArrowLeftIcon className="h-5 w-5 mr-2" />
          Back to Dashboard
        </button>
      </div>

      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex items-center mb-6">
          <div className="p-3 bg-primary-100 rounded-lg mr-4">
            <CloudArrowUpIcon className="h-8 w-8 text-primary-600" />
          </div>
          <div>
            <h1 className="text-2xl font-bold text-secondary-900">Upload Files</h1>
            <p className="text-secondary-500">Upload your files to the platform</p>
          </div>
        </div>

        <div 
          className={`border-2 border-dashed rounded-lg p-8 text-center ${
            dragActive ? 'border-primary-500 bg-primary-50' : 'border-secondary-300'
          }`}
          onDragEnter={handleDrag}
          onDragLeave={handleDrag}
          onDragOver={handleDrag}
          onDrop={handleDrop}
        >
          <CloudArrowUpIcon className="mx-auto h-12 w-12 text-secondary-400" />
          <p className="mt-4 text-secondary-900 font-medium">
            Drag and drop your files here, or
          </p>
          <label className="mt-2 inline-block">
            <input
              type="file"
              multiple
              className="hidden"
              onChange={handleFileInput}
            />
            <span className="text-primary-600 hover:text-primary-500 cursor-pointer">
              browse to choose files
            </span>
          </label>
        </div>

        {files.length > 0 && (
          <div className="mt-6">
            <h3 className="text-lg font-medium text-secondary-900 mb-4">Selected Files</h3>
            <div className="space-y-3">
              {files.map((file, index) => (
                <div 
                  key={index}
                  className="flex items-center justify-between bg-secondary-50 p-3 rounded-lg"
                >
                  <div className="flex items-center">
                    <CloudArrowUpIcon className="h-5 w-5 text-secondary-500 mr-3" />
                    <span className="text-secondary-900">{file.name}</span>
                    <span className="ml-2 text-secondary-500 text-sm">
                      ({(file.size / 1024 / 1024).toFixed(2)} MB)
                    </span>
                  </div>
                  <button
                    onClick={() => removeFile(index)}
                    className="text-secondary-500 hover:text-secondary-700"
                  >
                    Remove
                  </button>
                </div>
              ))}
            </div>

            <div className="mt-6">
              <button
                onClick={handleUpload}
                className="bg-primary-600 text-white px-4 py-2 rounded-lg hover:bg-primary-700 transition-colors"
              >
                Upload {files.length} {files.length === 1 ? 'file' : 'files'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Upload;
