import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeftIcon, CloudArrowUpIcon, XMarkIcon } from '@heroicons/react/24/outline';

const GENRES = [
  'Hip Hop',
  'Pop',
  'R&B',
  'Rock',
  'Electronic',
  'Jazz',
  'Classical',
  'Country',
  'Latin',
  'Other'
];

const Upload = () => {
  const navigate = useNavigate();
  const [dragActive, setDragActive] = useState(false);
  const [file, setFile] = useState(null);
  const [imagePreview, setImagePreview] = useState('');
  const [fileName, setFileName] = useState('');
  const [genre, setGenre] = useState('');
  const [error, setError] = useState('');

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const validateFile = (file) => {
    const validTypes = ['image/jpeg', 'image/png', 'image/gif'];
    if (!validTypes.includes(file.type)) {
      setError('Please upload an image file (JPEG, PNG, or GIF)');
      return false;
    }
    if (file.size > 5 * 1024 * 1024) {
      setError('File size should be less than 5MB');
      return false;
    }
    return true;
  };

  const handleFileSelect = (selectedFile) => {
    setError('');
    if (selectedFile && validateFile(selectedFile)) {
      setFile(selectedFile);
      setFileName(selectedFile.name);

      // Create image preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const droppedFile = e.dataTransfer.files[0];
    handleFileSelect(droppedFile);
  };

  const handleFileInput = (e) => {
    const selectedFile = e.target.files[0];
    handleFileSelect(selectedFile);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!file || !fileName.trim() || !genre) {
      setError('Please fill in all fields');
      return;
    }

    // Here you would typically send the data to your backend
    console.log('Submitting:', {
      file,
      fileName,
      genre
    });
  };

  const clearFile = () => {
    setFile(null);
    setFileName('');
    setImagePreview('');
    setError('');
  };

  return (
    <div className="min-h-screen bg-secondary-50 p-6">
      <div className="mb-6">
        <button
          onClick={() => navigate('/')}
          className="inline-flex items-center text-secondary-600 hover:text-secondary-900"
        >
          <ArrowLeftIcon className="h-5 w-5 mr-2" />
          Back to Dashboard
        </button>
      </div>

      <div className="bg-white rounded-lg shadow-sm p-6 max-w-2xl mx-auto">
        <div className="flex items-center mb-6">
          <div className="p-3 bg-primary-100 rounded-lg mr-4">
            <CloudArrowUpIcon className="h-8 w-8 text-primary-600" />
          </div>
          <div>
            <h1 className="text-2xl font-bold text-secondary-900">Upload File</h1>
            <p className="text-secondary-500">Add your file details</p>
          </div>
        </div>

        {error && (
          <div className="mb-4 p-3 bg-red-50 text-red-700 rounded-lg">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          {!file ? (
            <div 
              className={`border-2 border-dashed rounded-lg p-8 text-center ${
                dragActive ? 'border-primary-500 bg-primary-50' : 'border-secondary-300'
              }`}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
            >
              <CloudArrowUpIcon className="mx-auto h-12 w-12 text-secondary-400" />
              <p className="mt-4 text-secondary-900 font-medium">
                Drag and drop your image here, or
              </p>
              <label className="mt-2 inline-block">
                <input
                  type="file"
                  accept="image/*"
                  className="hidden"
                  onChange={handleFileInput}
                />
                <span className="text-primary-600 hover:text-primary-500 cursor-pointer">
                  browse to choose a file
                </span>
              </label>
              <p className="mt-2 text-sm text-secondary-500">
                Supported formats: JPEG, PNG, GIF (max 5MB)
              </p>
            </div>
          ) : (
            <div className="relative">
              <div className="aspect-video rounded-lg overflow-hidden bg-secondary-100">
                <img 
                  src={imagePreview} 
                  alt="Preview" 
                  className="w-full h-full object-contain"
                />
              </div>
              <button
                type="button"
                onClick={clearFile}
                className="absolute top-2 right-2 p-1 bg-white rounded-full shadow-sm hover:bg-secondary-50"
              >
                <XMarkIcon className="h-5 w-5 text-secondary-500" />
              </button>
            </div>
          )}

          <div className="space-y-4">
            <div>
              <label htmlFor="fileName" className="block text-sm font-medium text-secondary-700">
                File Name
              </label>
              <input
                type="text"
                id="fileName"
                value={fileName}
                onChange={(e) => setFileName(e.target.value)}
                className="mt-1 block w-full rounded-md border-secondary-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                placeholder="Enter file name"
              />
            </div>

            <div>
              <label htmlFor="genre" className="block text-sm font-medium text-secondary-700">
                Genre
              </label>
              <select
                id="genre"
                value={genre}
                onChange={(e) => setGenre(e.target.value)}
                className="mt-1 block w-full rounded-md border-secondary-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              >
                <option value="">Select a genre</option>
                {GENRES.map((g) => (
                  <option key={g} value={g}>{g}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="flex justify-end">
            <button
              type="submit"
              className="bg-primary-600 text-white px-4 py-2 rounded-lg hover:bg-primary-700 transition-colors"
              disabled={!file || !fileName.trim() || !genre}
            >
              Upload File
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Upload;
