import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import RootLayout from './components/RootLayout';
import Dashboard from './components/Dashboard';
import StreamAnalytics from './components/StreamAnalytics';
import Upload from './components/Upload';
import Profile from './components/Profile';
import Uploads from './components/Uploads';
import AccountSettings from './components/AccountSettings';
import Notifications from './components/Notifications';
import Security from './components/Security';
import VideoUploadForm from './components/VideoUploadForm';
import Analytics from './components/Analytics';
import { UserProvider } from './context/UserContext';
import './index.css';

function App() {
  useEffect(() => {
    const getTokenFromUrl = () => {
      const urlParams = new URLSearchParams(window.location.search);
      return urlParams.get('token');
    };

    const setTokenCookie = (token) => {
      if (token) {
        document.cookie = `xrftoken=${token}; path=/; max-age=86400`; // 24 hours
      }
    };

    const validateToken = async (token) => {
      if (!token) return;

      try {
        const response = await fetch('http://localhost:5000/validate', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error('Token validation failed');
        }

        const data = await response.json();
        console.log('Token validation successful:', data);
      } catch (error) {
        console.error('Error validating token:', error);
      }
    };

    const token = getTokenFromUrl();
    if (token) {
      setTokenCookie(token);
      validateToken(token);
    }
  }, []);

  return (
    <UserProvider>
      <Router>
        <div className="App h-screen overflow-hidden">
          <Routes>
            <Route path="/" element={<RootLayout />}>
              <Route index element={<Dashboard />} />
              <Route path="stream-analytics" element={<StreamAnalytics />} />
              <Route path="upload" element={<Upload />} />
              <Route path="upload-video" element={<VideoUploadForm />} />
              <Route path="profile" element={<Profile />} />
              <Route path="uploads" element={<Uploads />} />
              <Route path="analytics" element={<Analytics />} />
              <Route path="account-settings" element={<AccountSettings />} />
              <Route path="notifications" element={<Notifications />} />
              <Route path="security" element={<Security />} />
            </Route>
          </Routes>
        </div>
      </Router>
    </UserProvider>
  );
}

export default App;
