import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  DocumentTextIcon,
  CloudArrowUpIcon,
  VideoCameraIcon
} from '@heroicons/react/24/outline';

const Content = () => {
  const navigate = useNavigate();

  const quickActions = [
    {
      name: 'Upload Files',
      icon: CloudArrowUpIcon,
      bgColor: 'bg-primary-100',
      textColor: 'text-primary-600',
      description: 'Upload any type of file',
      link: '/upload'
    },
    {
      name: 'Upload Video',
      icon: VideoCameraIcon,
      bgColor: 'bg-orange-100',
      textColor: 'text-orange-600',
      description: 'Upload video content',
      link: '/upload'
    }
  ];

  const handleQuickAction = (action) => {
    if (action.link) {
      navigate(action.link);
    }
  };

  return (
    <div className="flex-1 overflow-y-auto bg-secondary-50 h-full">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 h-full py-8">
        <div className="bg-white rounded-lg shadow-sm h-full flex flex-col">
          <div className="px-6 py-4 border-b border-secondary-200">
            <h1 className="text-2xl font-bold text-secondary-900">Content Collection</h1>
            <p className="text-secondary-500">Manage and view all your content in one place</p>
          </div>

          <div className="p-6 flex-1 flex items-center justify-center">
            <div className="text-center">
              <DocumentTextIcon className="mx-auto h-12 w-12 text-secondary-400" />
              <h3 className="mt-2 text-sm font-medium text-secondary-900">No content</h3>
              <p className="mt-1 text-sm text-secondary-500">Get started by uploading your content</p>
              <div className="mt-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 max-w-2xl mx-auto">
                  {quickActions.map((action) => (
                    <button
                      key={action.name}
                      onClick={() => handleQuickAction(action)}
                      className="flex items-center p-4 bg-white rounded-lg shadow-sm hover:shadow-md transition-all duration-200 hover:translate-y-[-2px]"
                    >
                      <div className={`p-3 rounded-lg ${action.bgColor} ${action.textColor}`}>
                        <action.icon className="h-6 w-6" />
                      </div>
                      <div className="ml-4 text-left">
                        <p className="text-sm font-medium text-secondary-900">{action.name}</p>
                        <p className="text-xs text-secondary-500">{action.description}</p>
                      </div>
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Content;
