import React from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import Header from './Header';
import { 
  ArrowUpIcon, 
  ArrowDownIcon, 
  CurrencyDollarIcon, 
  PlayIcon, 
  ShoppingBagIcon, 
  ChartBarIcon, 
  DocumentIcon,
  CloudArrowUpIcon,
  VideoCameraIcon,
  DocumentTextIcon,
  TableCellsIcon,
  CodeBracketIcon
} from '@heroicons/react/24/outline';

const stats = [
  {
    name: 'Total Revenue',
    value: '$45,231.89',
    change: '+20.1%',
    changeType: 'increase',
    icon: CurrencyDollarIcon,
    link: '/revenue-analytics'
  },
  {
    name: 'Monthly Streams',
    value: '12,456',
    change: '+25.8%',
    changeType: 'increase',
    icon: PlayIcon,
  },
  {
    name: 'Sales',
    value: '1,429',
    change: '-3.2%',
    changeType: 'decrease',
    icon: ShoppingBagIcon,
  },
  {
    name: 'Conversion Rate',
    value: '4.5%',
    change: '+4.1%',
    changeType: 'increase',
    icon: ChartBarIcon,
  },
];

const quickActions = [
  {
    name: 'Upload File',
    icon: CloudArrowUpIcon,
    bgColor: 'bg-primary-100',
    textColor: 'text-primary-600',
    description: 'Upload any type of file'
  },
  {
    name: 'Upload Video',
    icon: VideoCameraIcon,
    bgColor: 'bg-orange-100',
    textColor: 'text-orange-600',
    description: 'Upload video content'
  }
];

const recentFiles = [
  {
    id: 1,
    name: 'quarterly_report_q3.xlsx',
    size: '1.2 MB',
    type: 'Spreadsheet',
    icon: TableCellsIcon,
    iconColor: 'text-green-600',
    iconBg: 'bg-green-100',
    modifiedAt: '2 hours ago',
    author: 'Sarah Chen'
  },
  {
    id: 2,
    name: 'api_documentation.md',
    size: '256 KB',
    type: 'Documentation',
    icon: CodeBracketIcon,
    iconColor: 'text-blue-600',
    iconBg: 'bg-blue-100',
    modifiedAt: '4 hours ago',
    author: 'Mike Johnson'
  },
  {
    id: 3,
    name: 'contract_template.docx',
    size: '845 KB',
    type: 'Document',
    icon: DocumentTextIcon,
    iconColor: 'text-indigo-600',
    iconBg: 'bg-indigo-100',
    modifiedAt: '6 hours ago',
    author: 'Lisa Wong'
  },
  {
    id: 4,
    name: 'project_proposal.pdf',
    size: '2.8 MB',
    type: 'PDF',
    icon: DocumentIcon,
    iconColor: 'text-red-600',
    iconBg: 'bg-red-100',
    modifiedAt: 'Yesterday',
    author: 'John Smith'
  }
];

const Dashboard = () => {
  const navigate = useNavigate();

  const handleQuickAction = (actionName) => {
    // Handle different quick actions
    console.log(`Executing quick action: ${actionName}`);
  };

  return (
    <div className="flex h-screen bg-secondary-50">
      <Sidebar />

      <div className="flex-1 flex flex-col overflow-hidden">
        <Header />

        <main className="flex-1 overflow-y-auto p-6">
          <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-4">
            {stats.map((item) => (
              <div
                key={item.name}
                className={`bg-white rounded-lg shadow-sm p-6 ${
                  item.link ? 'cursor-pointer hover:shadow-md hover:bg-secondary-50 transition-all duration-200' : ''
                }`}
                onClick={() => item.link && navigate(item.link)}
              >
                <div className="flex items-center justify-between">
                  <div className="p-2 bg-primary-100 rounded-lg">
                    <item.icon className="h-6 w-6 text-primary-600" />
                  </div>
                  <div className={`flex items-center ${
                    item.changeType === 'increase' ? 'text-green-600' : 'text-red-600'
                  }`}>
                    {item.changeType === 'increase' ? (
                      <ArrowUpIcon className="h-4 w-4" />
                    ) : (
                      <ArrowDownIcon className="h-4 w-4" />
                    )}
                    <span className="text-sm font-medium ml-1">{item.change}</span>
                  </div>
                </div>
                <p className="mt-4 text-sm font-medium text-secondary-600">{item.name}</p>
                <p className="mt-2 text-2xl font-semibold text-secondary-900">{item.value}</p>
              </div>
            ))}
          </div>

          {/* Quick Actions Section */}
          <div className="mt-6">
            <h2 className="text-lg font-medium text-secondary-900 mb-4">Quick Actions</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {quickActions.map((action) => (
                <button
                  key={action.name}
                  onClick={() => handleQuickAction(action.name)}
                  className="flex items-center p-4 bg-white rounded-lg shadow-sm hover:shadow-md transition-all duration-200 hover:translate-y-[-2px]"
                >
                  <div className={`p-3 rounded-lg ${action.bgColor} ${action.textColor}`}>
                    <action.icon className="h-6 w-6" />
                  </div>
                  <div className="ml-4 text-left">
                    <p className="text-sm font-medium text-secondary-900">{action.name}</p>
                    <p className="text-xs text-secondary-500">{action.description}</p>
                  </div>
                </button>
              ))}
            </div>
          </div>

          {/* Recent Files Section */}
          <div className="mt-6">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-medium text-secondary-900">Recent Files</h2>
              <button className="text-sm text-primary-600 hover:text-primary-700 font-medium">
                View All Files
              </button>
            </div>
            <div className="bg-white rounded-lg shadow-sm overflow-hidden">
              <div className="p-6">
                <div className="space-y-6">
                  {recentFiles.map((file) => (
                    <div key={file.id} className="flex items-center justify-between group hover:bg-secondary-50 p-2 rounded-lg transition-colors duration-150">
                      <div className="flex items-center flex-1">
                        <div className={`p-2 rounded-lg ${file.iconBg}`}>
                          <file.icon className={`h-6 w-6 ${file.iconColor}`} />
                        </div>
                        <div className="ml-4 flex-1">
                          <div className="flex items-center justify-between">
                            <p className="text-sm font-medium text-secondary-900">{file.name}</p>
                            <p className="text-xs text-secondary-500 ml-4">{file.size}</p>
                          </div>
                          <div className="flex items-center mt-1">
                            <p className="text-xs text-secondary-500">Modified {file.modifiedAt} by {file.author}</p>
                          </div>
                        </div>
                      </div>
                      <div className="ml-4 flex items-center space-x-2 opacity-0 group-hover:opacity-100 transition-opacity duration-150">
                        <button className="p-1 hover:bg-secondary-100 rounded">
                          <DocumentIcon className="h-4 w-4 text-secondary-500" />
                        </button>
                        <button className="p-1 hover:bg-secondary-100 rounded">
                          <ArrowDownIcon className="h-4 w-4 text-secondary-500" />
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
