import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ArrowLeftIcon, CloudArrowUpIcon, DocumentIcon } from '@heroicons/react/24/outline';

const FileUpload = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);

    const files = Array.from(e.dataTransfer.files);
    setSelectedFiles(prevFiles => [...prevFiles, ...files]);
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(prevFiles => [...prevFiles, ...files]);
  };

  const removeFile = (indexToRemove) => {
    setSelectedFiles(prevFiles => 
      prevFiles.filter((_, index) => index !== indexToRemove)
    );
  };

  return (
    <div className="min-h-screen bg-secondary-50 p-6">
      <div className="mb-6">
        <Link 
          to="/"
          className="inline-flex items-center text-secondary-600 hover:text-secondary-900"
        >
          <ArrowLeftIcon className="h-5 w-5 mr-2" />
          Back to Dashboard
        </Link>
      </div>

      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex items-center mb-6">
          <div className="p-3 bg-primary-100 rounded-lg mr-4">
            <CloudArrowUpIcon className="h-8 w-8 text-primary-600" />
          </div>
          <div>
            <h1 className="text-2xl font-bold text-secondary-900">Upload Files</h1>
            <p className="text-secondary-500">Upload your files by dragging & dropping or selecting them</p>
          </div>
        </div>

        {/* Upload Area */}
        <div 
          className={`border-2 border-dashed rounded-lg p-8 text-center ${
            isDragging ? 'border-primary-500 bg-primary-50' : 'border-secondary-300'
          }`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
        >
          <CloudArrowUpIcon className="h-12 w-12 mx-auto text-secondary-400" />
          <p className="mt-4 text-secondary-900 font-medium">
            Drag & drop your files here
          </p>
          <p className="mt-1 text-secondary-500">
            or
          </p>
          <label className="mt-4 inline-block">
            <input
              type="file"
              className="hidden"
              multiple
              onChange={handleFileSelect}
            />
            <span className="px-4 py-2 bg-primary-600 text-white rounded-lg cursor-pointer hover:bg-primary-700">
              Select Files
            </span>
          </label>
          <p className="mt-2 text-sm text-secondary-500">
            Supported file types: All files are supported
          </p>
        </div>

        {/* Selected Files List */}
        {selectedFiles.length > 0 && (
          <div className="mt-8">
            <h3 className="text-lg font-medium text-secondary-900 mb-4">Selected Files</h3>
            <div className="space-y-3">
              {selectedFiles.map((file, index) => (
                <div 
                  key={index}
                  className="flex items-center justify-between bg-secondary-50 p-3 rounded-lg"
                >
                  <div className="flex items-center">
                    <DocumentIcon className="h-6 w-6 text-secondary-500" />
                    <div className="ml-3">
                      <p className="text-sm font-medium text-secondary-900">{file.name}</p>
                      <p className="text-xs text-secondary-500">
                        {(file.size / 1024 / 1024).toFixed(2)} MB
                      </p>
                    </div>
                  </div>
                  <button
                    onClick={() => removeFile(index)}
                    className="text-secondary-400 hover:text-secondary-500"
                  >
                    Remove
                  </button>
                </div>
              ))}
              <button className="mt-4 px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700">
                Upload {selectedFiles.length} {selectedFiles.length === 1 ? 'File' : 'Files'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FileUpload;
