import React from 'react';
import {
  CogIcon,
  BellIcon,
  KeyIcon,
  ShieldCheckIcon,
} from '@heroicons/react/24/outline';

const Profile = () => {
  const user = {
    name: 'John Doe',
    role: 'Admin',
    avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80',
  };

  const settings = [
    {
      name: 'Account Settings',
      description: 'Update your account information',
      icon: CogIcon,
      href: '#',
    },
    {
      name: 'Notifications',
      description: 'Manage your notification preferences',
      icon: BellIcon,
      href: '#',
    },
    {
      name: 'Security',
      description: 'Update password and security settings',
      icon: KeyIcon,
      href: '#',
    },
    {
      name: 'Privacy',
      description: 'Manage your privacy preferences',
      icon: ShieldCheckIcon,
      href: '#',
    },
  ];

  return (
    <div className="flex-1 overflow-y-auto bg-secondary-50">
      <div className="max-w-4xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        {/* Profile Header */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="sm:flex sm:items-center sm:justify-between">
            <div className="sm:flex sm:space-x-5">
              <div className="flex-shrink-0">
                <img
                  className="mx-auto h-20 w-20 rounded-full"
                  src={user.avatar}
                  alt={user.name}
                />
              </div>
              <div className="mt-4 text-center sm:mt-0 sm:pt-1 sm:text-left">
                <p className="text-xl font-bold text-secondary-900">{user.name}</p>
                <p className="text-sm font-medium text-secondary-500">{user.role}</p>
              </div>
            </div>
            <div className="mt-5 flex justify-center sm:mt-0">
              <button className="flex justify-center items-center px-4 py-2 border border-secondary-300 shadow-sm text-sm font-medium rounded-md text-secondary-700 bg-white hover:bg-secondary-50">
                Edit Profile
              </button>
            </div>
          </div>
        </div>

        {/* Settings */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h2 className="text-lg font-medium text-secondary-900 mb-4">Settings</h2>
          <div className="divide-y divide-secondary-200">
            {settings.map((setting) => (
              <div
                key={setting.name}
                className="py-4 flex items-center justify-between hover:bg-secondary-50 px-4 rounded-lg cursor-pointer"
              >
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <setting.icon className="h-6 w-6 text-secondary-400" />
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-secondary-900">{setting.name}</p>
                    <p className="text-sm text-secondary-500">{setting.description}</p>
                  </div>
                </div>
                <div>
                  <button className="text-secondary-400 hover:text-secondary-500">
                    <span className="sr-only">Edit {setting.name.toLowerCase()}</span>
                    <CogIcon className="h-5 w-5" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
