import React from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import { ArrowUpIcon, ArrowDownIcon, CurrencyDollarIcon, UsersIcon, ShoppingBagIcon, ChartBarIcon } from '@heroicons/react/24/outline';

const stats = [
  {
    name: 'Total Revenue',
    value: '$45,231.89',
    change: '+20.1%',
    changeType: 'increase',
    icon: CurrencyDollarIcon,
  },
  {
    name: 'Active Users',
    value: '2,338',
    change: '+15.3%',
    changeType: 'increase',
    icon: UsersIcon,
  },
  {
    name: 'Sales',
    value: '1,429',
    change: '-3.2%',
    changeType: 'decrease',
    icon: ShoppingBagIcon,
  },
  {
    name: 'Conversion Rate',
    value: '4.5%',
    change: '+4.1%',
    changeType: 'increase',
    icon: ChartBarIcon,
  },
];

const Dashboard = () => {
  return (
    <div className="flex h-screen bg-secondary-50">
      <Sidebar />

      <div className="flex-1 flex flex-col overflow-hidden">
        <Header />

        <main className="flex-1 overflow-y-auto p-6">
          <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-4">
            {stats.map((item) => (
              <div
                key={item.name}
                className="bg-white rounded-lg shadow-sm p-6 hover:shadow-md transition-shadow duration-200"
              >
                <div className="flex items-center justify-between">
                  <div className="p-2 bg-primary-100 rounded-lg">
                    <item.icon className="h-6 w-6 text-primary-600" />
                  </div>
                  <div className={`flex items-center ${
                    item.changeType === 'increase' ? 'text-green-600' : 'text-red-600'
                  }`}>
                    {item.changeType === 'increase' ? (
                      <ArrowUpIcon className="h-4 w-4" />
                    ) : (
                      <ArrowDownIcon className="h-4 w-4" />
                    )}
                    <span className="text-sm font-medium ml-1">{item.change}</span>
                  </div>
                </div>
                <p className="mt-4 text-sm font-medium text-secondary-600">{item.name}</p>
                <p className="mt-2 text-2xl font-semibold text-secondary-900">{item.value}</p>
              </div>
            ))}
          </div>

          {/* Recent Activity Section */}
          <div className="mt-6">
            <h2 className="text-lg font-medium text-secondary-900 mb-4">Recent Activity</h2>
            <div className="bg-white rounded-lg shadow-sm overflow-hidden">
              <div className="p-6">
                <div className="space-y-6">
                  {[1, 2, 3].map((item) => (
                    <div key={item} className="flex items-center">
                      <div className="w-2 h-2 bg-primary-500 rounded-full"></div>
                      <div className="ml-4">
                        <p className="text-sm font-medium text-secondary-900">New user registration</p>
                        <p className="text-sm text-secondary-500">2 minutes ago</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
